mapper.select:
select COUNT(0) AS warning_count
from warnings w left join models m on w.model_id = m.model_id
where m.device_id = #{param.deviceId} and m.model_type = #{param.deviceType} and w.model_id = #{param.modelId} and w.warning_level =#{param.warningLevel} and w.warning_status IN (0,1,3)

    AND w.start_time >= #{param.startTime}


    AND w.end_time <= #{param.endTime}

SELECT
COUNT(CASE WHEN w.warning_status = 0 AND w.warning_level = 1 THEN 1 ELSE NULL END) AS warningLevel1waitDone,
COUNT(CASE WHEN w.warning_status = 0 AND w.warning_level = 2 THEN 1 ELSE NULL END) AS warningLevel2waitDone,
COUNT(CASE WHEN w.warning_status = 1 AND w.warning_level = 1 THEN 1 ELSE NULL END) AS warningLevel1waitHangUp,
COUNT(CASE WHEN w.warning_status = 1 AND w.warning_level = 2 THEN 1 ELSE NULL END) AS warningLevel2waitHangUp,
COUNT(CASE WHEN w.warning_status = 3 AND w.warning_level = 1 THEN 1 ELSE NULL END) AS warningLevel1waitCloseWait,
COUNT(CASE WHEN w.warning_status = 3 AND w.warning_level = 2 THEN 1 ELSE NULL END) AS warningLevel2waitCloseWait
FROM warnings w where model_id = #{modelId}

    AND w.start_time >= #{startTime}


    AND w.end_time <= #{endTime}

SELECT w.*, m.device_id, m.model_type FROM warnings w JOIN models m ON w.model_id = m.model_id WHERE ( (m.model_type = 0 AND m.device_id IN (SELECT pf.id FROM pv_farm pf WHERE (pf.id = #{param.pvFarmId} OR #{param.pvFarmId} IS NULL))) OR (m.model_type = 1 AND m.device_id IN (SELECT cb.id FROM combiner_box cb WHERE cb.box_id IN (SELECT bt.id FROM box_trans bt WHERE bt.pv_farm_id = #{param.pvFarmId} OR #{param.pvFarmId} IS NULL))) OR (m.model_type = 2 AND m.device_id IN (SELECT i.id FROM inverter i WHERE i.box_id IN (SELECT bt.id FROM box_trans bt WHERE bt.pv_farm_id = #{param.pvFarmId} OR #{param.pvFarmId} IS NULL))) );
AND w.start_time >= #{param.startTime}
AND w.end_time <= #{param.endTime}
ORDER BY w.start_time DESC

SELECT w.warning_description, CASE WHEN m.model_type = 0 THEN (SELECT pf.pv_farm_name FROM pv_farm pf WHERE pf.id = m.device_id) WHEN m.model_type = 1 THEN (SELECT cb.combiner_box_name FROM combiner_box cb WHERE cb.id = m.device_id) WHEN m.model_type = 2 THEN (SELECT i.inverter_name FROM inverter i WHERE i.id = m.device_id) ELSE NULL END AS device_name FROM warnings w JOIN models m ON w.model_id = m.model_id
AND m.device_id = #{param.deviceId}
AND m.model_type = #{param.deviceType}
AND w.start_time >= #{param.startTime}
AND w.end_time <= #{param.endTime}
AND w.warning_status != 4
GROUP BY w.warning_description, m.model_type, m.device_id

SELECT w.*, CASE WHEN m.model_type = 0 THEN (SELECT pf.pv_farm_name FROM pv_farm pf WHERE pf.id = m.device_id) WHEN m.model_type = 1 THEN (SELECT cb.combiner_box_name FROM combiner_box cb WHERE cb.id = m.device_id) WHEN m.model_type = 2 THEN (SELECT i.inverter_name FROM inverter i WHERE i.id = m.device_id) ELSE NULL END AS device_name FROM warnings w JOIN models m ON w.model_id = m.model_id
AND m.device_id = #{param.deviceId}
AND m.model_type = #{param.deviceType}
AND w.start_time >= #{param.startTime}
AND w.end_time <= #{param.endTime}
AND w.warning_description = #{param.warningDescription}
AND w.warning_status != 4
ORDER BY w.start_time DESC
